import React from 'react'
import CardTemplate from './CardTemplate'
import P from './P'
import SmallContainer from './SmallContainer'
import Input from './Input'
import Button from './Button'
import Tertiary from './Colors'

import { useDispatch } from 'react-redux';
import { increment } from '../actions'

import { CreateCardData } from '../data/CreateCardData.js'

class Card extends React.Component {
	render() 
	{
	const {id, question, dataEntryId, buttonText } = this.props;


	return(
		<CardTemplate>
			<SmallContainer background width='200'>

			<P big>{question}</P>	

			<Input></Input>

			<Button color={Tertiary}
				onClick={()=> { CreateCardData[0].answered = false } }
				>{buttonText}</Button>				
			</SmallContainer>
		</CardTemplate>			
	)
	}

}

export default Card;
