import styled from 'styled-components';
import {Secondary, Primary} from './Colors.js'

export default styled.div`
	display: flex;
	flex-flow: row wrap;
	justify-content: center;
	padding-right: 15px;
  padding-left: 15px;
  padding-bottom: 15px;
  margin: 0 auto;
  background-color: ${Secondary};
  	//border: 1px solid ${Primary};

  border: ${props => (
   props.border ? '1px solid' : '0px'
  )};

  width: ${(props) => {
			if (props.width > 0) {
  			let x = props.width + 'px'
  			return x;
  		}	
  	}
  };

`;
