import styled from 'styled-components';

export default styled.div`

	display: flex;
	flex-flow: row wrap;
	justify-content: center;
	padding-right: 15px;
  padding-left: 15px;
  padding-bottom: 15px;


  border: ${props => (
   props.border ? '1px solid' : '0px'
  )};

  width: ${(props) => {
			if (props.width > 0) {
  			let x = props.width + 'px'
  			return x;
  		}	
  	}
  };

`;
