import React from 'react'
import styled from 'styled-components';

import { useSelector, useDispatch } from 'react-redux';
import { expandNav } from '../actions'


const Styles = styled.div`
	.nav {
		background-color: white;
		color: white;
		width: 15%;
		position: fixed;
		height: 100%;
		top: 0;
		left: 0;
		transition: all 0.2s;
		box-shadow: 0px 6px 17px -8px rgba(0,0,0,0.75);
	}

	.expand {
		width: 100%;
	}

	.collapse {
		width: 15%;
	}

	.topNavbar {
		width: 100%;
		position: fixed;
		top: 0;
		background-color: gray;
		display: flex-wrap;
		flex-wrap: wrap;
	}

	.topNavbar ul li a {
		display: flex;
		flex-wrap: wrap;
		color: red
	}
`;

const NavigationBar = () => {
		const isNavExpanded = useSelector(state => state.isNavExpanded)
		const dispatch = useDispatch();


		return(
			<Styles>
			<div className='topNavbar'>
				<ul>
					<li><a href='#nomatch'>boobies</a></li>
					<li><a href='#nomatch'>boobies</a></li>
					<li><a href='#nomatch'>boobies</a></li>
				</ul>
			</div>

			<div className={isNavExpanded ? 'nav expand' : 'nav collapse'}>


				<button onClick={() => {
					dispatch(expandNav())
					console.log(isNavExpanded);
				}
				}>-</button>


				<ul>
					<li><a href='#notvalid'>My Account</a></li>
					<li><a href='#notvalid'>My Characters</a></li>
					<li><a href='#notvalid'>About MTTH</a></li>
					<li><a href='#notvalid'>Contact</a></li>
				</ul>
			</div>
			</Styles>
		)
}

export default NavigationBar;
