import React from 'react';

import { Button, H1, Navbar, NavItems, NavItem, Wrapper } from './components'

import Marvel from './containers/Marvel'


class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {navbarEnabled: false};
	};

	toggleNavbar() {
		this.setState({ navbarEnabled: !this.state.navbarEnabled  })
		console.log(this.state.navbarEnabled);
	};

	render() {
	return(
		<Wrapper>
			<Button onClick={ () => { this.toggleNavbar() } }>toggle nav</Button>
			<Navbar position='fixed' 
				width={this.state.navbarEnabled ? '400px' : '0px'} 
				height='100vh' 
				background='black'
				display={this.state.navbarEnabled ? 'none' : 'flex'} >
				<NavItems justify='left'>
				<H1 big borderbottom padding='50px 0px 0px 0px'>THE WATCH ORDER</H1>
					<NavItem><H1>Phase 1</H1></NavItem>
					<NavItem>Iron Man</NavItem>
					<NavItem>Iron Man</NavItem>
					<NavItem>Iron Man</NavItem>
				</NavItems>
			</Navbar>

			<Marvel marginleft={this.state.navbarEnabled ? '400px' : '0px'} />
		</Wrapper>
	);
	}
}

export default App;
