import styled from 'styled-components';

export default styled.div`
	font-size: ${props => (props.big ? '1.5rem' : '1.3rem')};
	font-weight: bold;
	width: 100vw;
	text-align: center;
	padding: 5px;
	border-bottom: 2px solid red;
`;
