import styled from 'styled-components';
import { Primary } from './Colors.js'

const list = styled.li`
	display: inline-block;
	//margin-left: 20px;
	text-align: center;
	//padding: 0px 5px 0px 5px;
	color: gray;

	transition: all 0.1s;

	font-size: 0.9rem;

	text-decoration: none;

	width: fill;



	&:hover {
		border-bottom: 2px solid ${Primary};
		cursor: pointer;
		transform: scale(1.15);

		a { color: white }
	}

	a {
		color: #aaa;
			text-decoration: none;
	}

	&, button {
		background: none;
		border: none;
	}


	h1 {
		padding-bottom: -20px;
	}
`;

const newList = list.withComponent('button');


export default newList;
