import React from 'react';

import {ParallaxBackground, Button, H1, Img, Navbar, NavItems, NavItem, SmallContainer, Wrapper } from './components'

import Timeline from './containers/Timeline'


import listicon from './assets/listicon.png'

import Phase1 from './data/Phase1.js'

import Card from './containers/Card'

import img1 from './assets/bgimg2.jpg'
import marveledition from './assets/marveledition.png'
let phaseText = ' \n \n P \n H \n A \n S \n E \xa0 \n \n O \n N \n E \n \n \n '
let phaseText2 = ' \n \n P \n H \n A \n S \n E \xa0 \n \n T \n W \n O \n \n \n '




class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {navbarEnabled: true};
	};

	toggleNavbar() {
		this.setState({ navbarEnabled: !this.state.navbarEnabled  })
		console.log(this.state.navbarEnabled);
	};



	render() {
	const newArray = Phase1.map((user, i) => {
		return <Card name={user.name} />;
		console.log(newArray);
	})

	return(
		<Wrapper>				
			<Navbar position='fixed' 
				width={this.state.navbarEnabled ? '400px' : '0px'} 
				height='100vh' 
				background='black'>
				<NavItems justify='left' display={this.state.navbarEnabled ? 'flex' : 'none'}>
					<H1 big padding='50px 0px 0px 0px'>THE WATCH ORDER</H1>
					<Img src={marveledition} width='80%' marginleft='50px'/>
					<NavItem><H1>Phase 1</H1></NavItem>
					<NavItem><H1>Phase 2</H1></NavItem>
					<NavItem><H1>Phase 3</H1></NavItem>
					<NavItem><H1>Phase 4</H1></NavItem>
					<NavItem><H1>Phase 5+</H1></NavItem>
				</NavItems>
			</Navbar>

			<SmallContainer marginleft= {this.state.navbarEnabled ? '400px' : '0px'}>

				<SmallContainer height='48px' width='48px' position='fixed' zindex='55'
					marginleft={this.state.navbarEnabled ? '-399px' : '0px'}>
					<Button onClick={ () => { this.toggleNavbar() } } color='transparent'>
						<img src={listicon} alt='nav icon'/>
					</Button>
				</SmallContainer>



				<Timeline phasetext={phaseText}>
					<Card name={Phase1[0].name} />
					<Card />
					<Card />
					<Card />
					<Card />
					<Card />
				</Timeline>

				<ParallaxBackground src={img1} height='40vh' width='100%'>

				</ParallaxBackground>

				<Timeline phasetext={phaseText2}>
					<Card />
					<Card />
					<Card />
					<Card />
					<Card />
					<Card />
				</Timeline>
			</SmallContainer>
		</Wrapper>
	);
	}
}

export default App;
