import React from 'react'
import styled from 'styled-components';

import { useSelector, useDispatch } from 'react-redux';
import { expandNav } from '../actions'


const Styles = styled.div`
	.nav {
		background-color: black;
		color: white;
		width: 15%;
		height: 25%;
		position: ;
		top: 0;
		left: 0;
	}

	.expand {
		width: 100%;
	}

	.collapse {
		width: 15%;
	}
`;

const NavigationBar = () => {
		const isNavExpanded = useSelector(state => state.expandNav)
		const dispatch = useDispatch();


		return(
			<Styles>
			<div className={isNavExpanded ? 'expand' : 'collapse'} className='nav'>


				<button onClick={() => dispatch(expandNav())}>-</button>


				<ul>
					<li><a href='#notvalid'>My Account</a></li>
					<li><a href='#notvalid'>My Characters</a></li>
					<li><a href='#notvalid'>About MTTH</a></li>
					<li><a href='#notvalid'>Contact</a></li>
				</ul>
			</div>
			</Styles>
		)
}

export default NavigationBar;
