import styled from 'styled-components';
import { Border, Background, Primary } from './Colors'

export default styled.div`
	display: flex;
	flex-flow: row wrap;
  text-align: center;
  justify-content: center;


  border: ${props => (
   props.border ? '5px solid' : '0px'
  )};

  border-color: ${Border};

  border-radius: 0.5;

  background-color: ${(props) => {
    if (props.background)
    if (props.background) {
      return Primary;
    } 
    else return null;
  }};

  box-shadow: ${(props) => {
    if (props.background)
    if (props.background) {
      return '0px 10px 10px -6px rgba(0,0,0,0.55);';
    } 
    else return null;
  }};



  width: ${(props) => {
      if (props.width)
			if (props.width.replace(/\D/g, '') > 0) {
  			let x = props.width;
  			return x;
  		}	
  	}
  };

  height: ${(props) => {
      if (props.height)
      if (props.height.replace(/\D/g, '') > 0) {
        let x = props.height;
        return x;
      } 
    }
  };

  margin-left: ${(props) => {
    if (props.marginleft) 
      return props.marginleft; else return null;
    }
    };

  margin-right: ${(props) => {
    if (props.marginright) 
    return props.marginright; else return null;
    }
    };


`;
