import React from 'react'
import styled from 'styled-components';

style = styled.h1`
	display: block;
	font-size: ${props => (props.big ? '2rem' : '1.2rem')};
	font-weight: bold;
	text-align: center;
	left: 50%;
	right: 50%;
	padding: 0;

`;

function H1() {
	return (
		<style>
			this.props.child;
		</style>
		)
}

export default H1;
