import styled from 'styled-components';

export default styled.h1`
	display: inline-block;
	font-size: ${props => (props.big ? '2rem' : '1.2rem')};
	font-weight: bold;
	text-align: center;
	left: 50%;
	right: 50%;
	padding: 0;
`;
