import styled from 'styled-components';
import { Secondary, Background } from './Colors'

export default styled.div`
	font-size: ${props => (props.big ? '1.5rem' : '1.3rem')};
	font-weight: bold;
	width: 100vw;
	text-align: center;
	padding: 5px;
	margin-bottom: 10px;
	margin-top: 10px;
	border: 2px solid ${Secondary};
	background-color: ${Background}
`;
