import styled from 'styled-components';
import { Primary, Secondary } from './Colors.js'

export default styled.button`
	background-color: ${props => props.color === 'secondary' ? Secondary : Primary};
	text-align: center;
	//white-space: nowrap;
	border: 1px solid black;
	transition: all 0.1s;

	margin: 10px;

	display: block;

	width: 100%;

	&:hover {
		transform: scale(1.1);
	}
`;
