import React from 'react';
import {H1, SmallContainer, P} from '../components'
import styled from 'styled-components';

const Style = styled.div`
	display: flex;
	flex-direction: column;
	align-self: flex-start;
	//justify-content: center;
	margin: 1rem;
	margin-top: 1rem;
	padding: 10px;
	position: relative;
	border: 5px solid black;
	width: 45%;
	height: 300px;

	&:nth-of-type(even) {
		align-self: flex-end;
	}

	& .circle {
		background-color: #2E2E2E;
		border: 3px solid orange;
		border-radius: 50%;
		position: absolute;
		width: 20px;
		height: 20px;
		z-index: 1;
		right: -11%;
		top: -10%;
	}

	&:nth-of-type(even) .circle {
		left: -11%;
		border-color: red;		
	}

	&:nth-of-type(even)	.date {
		position: absolute;
		left: -15%;
		top: -10%;
		color: white;
		z-index: 2;
	}



`;

class TimelineItem extends React.Component {
	render() {
		return(
	    	<Style>

	    		<H1 borderbottom>{this.props.title}</H1>

	    		<br/>
	    		<P className='date'>{this.props.date}</P>
	    		<span className='circle'></span>
	    	</Style>		    
		)
	;}
}

export default TimelineItem;
