import React from 'react';

import {ParallaxBackground, Button, H1, Img, Navbar, NavItems, NavItem, SmallContainer, ScrollContainer, Wrapper } from './components'

import Timeline from './containers/Timeline'


import listicon from './assets/listicon.png'

import {Phase1, Phase2, Phase3, Phase4} from './data/Phases.js'

import Card from './containers/Card'

import lines from '../assets/lines.png'
import img1 from './assets/bgimg2.jpg'
import marveledition from './assets/marveledition.png'
let phaseText = ' \n \n P \n H \n A \n S \n E \xa0 \n \n O \n N \n E \n \n \n '
let phaseText2 = ' \n \n P \n H \n A \n S \n E \xa0 \n \n T \n W \n O \n \n \n '
let phaseText3 = ' \n \n P \n H \n A \n S \n E \xa0 \n \n T \n H \n R \n E \n E \n '
let phaseText4 = ' \n \n P \n H \n A \n S \n E \xa0 \n \n F \n O \n U \n R \n \n '
let phaseText5 = ' \n \n P \n H \n A \n S \n E \xa0 \n \n F \n I \n V \n E \n \n '




class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			isMobile: false,
			navbarEnabled: false};

		this.navbarRef = React.createRef();
	};

	toggleNavbar() {
		this.setState({ navbarEnabled: !this.state.navbarEnabled  })
		console.log( this.state.navbarEnabled);
		console.log( this.navbarRef.current.attributes.display);
	};

	componentDidMount() {
		if ( this.navbarRef.current.attributes.display === 'none') {
			this.setState({ isMobile: 'true'});
		}

		if (this.state.isMobile) {
			this.setState({ navbarEnabled: false});
		}
		else {
			this.setState({ navbarEnabled: true});
		}
	}

	render() {
		const phase1Cards = Phase1.map((user, i) => {
		return <Card name={user.name} release={user.release} />;
		})

		const phase2Cards = Phase2.map((user, i) => {
		return <Card name={user.name} release={user.release} />;
		})

		const phase3Cards = Phase3.map((user, i) => {
		return <Card name={user.name} release={user.release} />;
		})

		const phase4Cards = Phase4.map((user, i) => {
		return <Card name={user.name} release={user.release} />;
		})


	return(
		<Wrapper>				
			<Navbar position='fixed' 
				width={this.state.navbarEnabled ? '400px' : '0px'} 
				height='100vh' 
				background='black'>

				<NavItems ref={this.navbarRef} display={this.state.navbarEnabled ? 'flex' : 'none'} justify='left'>
					<H1 big padding='50px 0px 0px 0px'>THE WATCH ORDER</H1>
					<Img src={marveledition} width='80%' marginleft='50px'/>
					<NavItem href='#phase1'><H1>Phase 1</H1></NavItem>
					<NavItem href='#phase2'><H1>Phase 2</H1></NavItem>

					<NavItem href='#phase3'><H1>Phase 3</H1></NavItem>
					<NavItem href='#phase4'><H1>Phase 4</H1></NavItem>
					<NavItem href='#phase5'><H1>Phase 5+</H1></NavItem>
				</NavItems>
			</Navbar>

			{/*Body Container*/}	
			<SmallContainer marginleft= {this.state.navbarEnabled ? '400px' : '0px'}>

				{/*Navigation Button*/}
				<SmallContainer height='48px' width='48px' position='fixed' zindex='55'
					marginleft={this.state.navbarEnabled ? '-399px' : '0px'}>
					<Button onClick={ () => { this.toggleNavbar() } } color='transparent'>
						<img src={listicon} alt='nav icon'/>
					</Button>
				</SmallContainer>

				<div id='phase1' />
				<Timeline phasetext={phaseText}>
					{phase1Cards}
				</Timeline>

				<ParallaxBackground src={img1} height='40vh' width='100%'></ParallaxBackground>

				<div id='phase2' />
				<Timeline phasetext={phaseText2}>
					{phase2Cards}
				</Timeline>

				<div id='phase3' />
				<Timeline id='phase3' phasetext={phaseText3}>
					{phase3Cards}
				</Timeline>

				<div id='phase4' />
				<Timeline phasetext={phaseText4}>
					{phase4Cards}
				</Timeline>

				<div id='phase5' />
				<Timeline phasetext={phaseText5}>

				</Timeline>

			</SmallContainer>

		</Wrapper>
	);
	}
}

export default App;
