import React from 'react';

import { Button, H1, Navbar, NavItems, NavItem, SmallContainer, Wrapper } from './components'

import Marvel from './containers/Marvel'

import listicon from './assets/listicon.png'


class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {navbarEnabled: true};
	};

	toggleNavbar() {
		this.setState({ navbarEnabled: !this.state.navbarEnabled  })
		console.log(this.state.navbarEnabled);
	};

	render() {
	return(
		<Wrapper>				
			<Navbar position='fixed' 
				width={this.state.navbarEnabled ? '400px' : '0px'} 
				height='100vh' 
				background='black'
				display={this.state.navbarEnabled ? 'flex' : 'none'} >
				<NavItems justify='left'>
				<H1 big borderbottom padding='50px 0px 0px 0px'>THE WATCH ORDER</H1>
					<NavItem><H1>Phase 1</H1></NavItem>
					<NavItem>Iron Man</NavItem>
					<NavItem>Iron Man</NavItem>
					<NavItem>Iron Man</NavItem>
				</NavItems>
			</Navbar>

			<SmallContainer marginleft={this.state.navbarEnabled ? '400px' : '0px'}>

				<SmallContainer height='48px' width='48px'
					marginleft={this.state.navbarEnabled ? '-60px' : '0px'}>
					<Button onClick={ () => { this.toggleNavbar() } } color='transparent'>
						<img src={listicon}/>
					</Button>
				</SmallContainer>



				<Timeline />
			</SmallContainer>
		</Wrapper>
	);
	}
}

export default App;
