import React from 'react'
import styled from 'styled-components';
import { Primary, Secondary } from './Colors.js'

export default styled.button`
	//background-color: ${props => props.color === 'secondary' ? Secondary : Primary};

	background-color: ${Primary};
	text-align: center;
	//white-space: nowrap;
	border: 1px solid black;
	transition: all 0.1s;

	margin: 1px;
	margin-top: 10px;

	width: 100%;


	//border: 2px solid ${Primary};
	border-radius: 2px;
	box-shadow: 0px 10px 10px -6px rgba(0,0,0,0.55);

	&, a {
		color: #333;
	}

	&:hover {
		transform: scale(1.1);
		color: white;
		background-color: ${Secondary}
		a { color: white;}
	}

	&:focus {

		outline: 0;
	}

`;
