import React from 'react'
import {Component} from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { expandNav } from '../actions'
import img from '../assets/bx-menu.svg';

const Styles = styled.div`
	.topNavbar {
		width: 100%;
		position: fixed;
		top: 0;
		background-color: white;
		display: flex-wrap;
		flex-wrap: wrap;
		box-shadow: 0px 6px 17px -8px rgba(0,0,0,0.75);
		z-index: 2;
	}

	.topNavbar ul li {
		display: inline-block;
	}

	.topNavbar ul li a {
		margin: 1rem;
	}
`;
		const isNavExpanded = useSelector(state => state.isNavExpanded)
		const dispatch = useDispatch();

export const TopNavigationBar = () => {
		return(
			<Styles>
				<div className='topNavbar'>
					<img id='toggleNav' src={ img } alt ='toggle navigation' onClick={() => {
						dispatch(expandNav())
						console.log(isNavExpanded);
						}
					}></img>
					<ul className=''>
						<li><a href='#nomatch'>My Tabletop Hero</a></li>
					</ul>
				</div>
			</Styles>
		)

}
