import React from 'react';
import styled from 'styled-components';

import card from '../assets/card.png'
import card2 from '../assets/card_cap.png'
import card3 from '../assets/card_iron2.png'

import img1 from '../assets/bgimg.jpg'

import {H1, SmallContainer} from '../components'

const Style = styled.div`
	background-image: url(${card});

	background-size: cover;
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
	position: relative;
	width: 360px;
	height: 130px;
	margin: 8px 0;
	transition: all 0.5s;
	box-shadow: 0px 0px 10px -2px rgba(200,200,200,0.2);
	border-bottom: 1px solid white;

	& .date {
		margin-top: 45px;
		font-size: 0.8rem;
		margin-right: 10px;
		margin-left: auto;
	}

	&:nth-child(odd) {
		align-self: flex-end;
		background-image: url(${card3});

		.date {			
			margin-right: auto;
			margin-left: 10px;
		}
	}

	img {

	}

	&:hover {
		box-shadow: 0px 0px 10px -2px rgba(255,255,255,0.5);

	}



`;

class Card extends React.Component {
	render() {
	return(
		<ParallaxBackground src={img1} height='40vh' width='100%'>


		<Style>
		<SmallContainer marginright='auto' marginleft='auto'>
			<H1 className='title'>IRON MAN</H1>
		</SmallContainer>

		<p>"I am Iron Man"</p>

		<SmallContainer className='date'>
			<p>May 2008</p>
		</SmallContainer>

		</Style>
		</ParallaxBackground>
	)
	}
}

export default Card;
