import styled from 'styled-components';

export default styled.div`

  margin-left: ${(props) => {
    if (props.margin) 
    switch(props.margin) {
      case 'left': return 'auto';
      default:
    }
    }};

  margin-right: ${(props) => {
    if (props.margin) 
    switch(props.margin) {
      case 'right': return 'auto';
      default:
    }
    }};



	display: flex;
	flex-flow: row wrap;


  text-align: center;
  justify-content: center;

  border: ${props => (
   props.border ? '1px solid' : '0px'
  )};

  background-color: ${(props) => {
    if (props.background)
    if (props.background) {
      return 'rgba(0,0,0,0.2)';
    } 
    else return null;
  }};

  box-shadow: ${(props) => {
    if (props.background)
    if (props.background) {
      return '0px 10px 10px -6px rgba(0,0,0,0.55);';
    } 
    else return null;
  }};



  width: ${(props) => {
      if (props.width)
			if (props.width.replace(/\D/g, '') > 0) {
  			let x = props.width;
  			return x;
  		}	
  	}
  };

`;
