import React from 'react';
import {Container, Wrapper, Header, H1, SmallContainer, ParallaxBackground} from '../components'

import ironManPoster from '../assets/ironManPoster.jpg'

import Card from './Card'
import {MoviesList} from '../data/MoviesList.js'

import SearchBox from '../components/SearchBox'


class WatchOrder extends React.Component {
	constructor( props ) {
		super(props); 
		this.state = {
			searchField: '',
		}
	}

	onSearchChange = (event) => {
		this.setState({searchField: event.target.value})
	}

	render() {
		const newArray = MoviesList.filter(user => 
			user.movieName.toLowerCase().includes(this.state.searchField.toLowerCase())
		);

		const cardsArray = newArray.map((currentValue, i) => {
			const cardColor = newArray[i].phase > 1 ? '#333' : '#000';
			return <Card movieName={newArray[i].movieName} releaseDate={newArray[i].release} background={cardColor}/>
		});

		return(
		    <Wrapper>
			    <Header>
			    	<H1 big>MCU OVERVIEW</H1>		    	
			    </Header>
		    </Wrapper>
		);
	}

}

export default WatchOrder;
