import React from 'react'
import styled from 'styled-components';

import { useSelector, useDispatch } from 'react-redux';
import { expandNav } from '../actions'


const Styles = styled.div`
	.nav {
		background-color: white;
		color: white;
		width: 15%;
		position: fixed;
		height: 100%;
		top: 0;
		left: 0;
		transition: all 0.2s;
		box-shadow: 0px 6px 17px -8px rgba(0,0,0,0.75);
		padding-top: 3rem;
	}

	.nav ul {
		text-align: center;
	}

	.nav ul li {
		padding-top: 2rem;
	}

	.expand {
		width: 250px;
		display: block;
	}

	.collapse {
		width: 0px;
		display: none;
	}

	.topNavbar {
		width: 100%;
		position: fixed;
		top: 0;
		background-color: white;
		display: flex-wrap;
		flex-wrap: wrap;
		box-shadow: 0px 6px 17px -8px rgba(0,0,0,0.75);
		z-index: 2;
	}

	.topNavbar ul li {
		display: inline-block;
	}

	.topNavbar ul li a {
		margin: 1rem;
	}

	#toggleNav {
		float: right;
		margin: 1rem;
	}

	a {
		font-size: 1.2rem;
		text-decoration: none;
		color: black;
	}

	.contentBody {
		border: solid 2px black;
	}
`;

const NavigationBar = () => {
		const isNavExpanded = useSelector(state => state.isNavExpanded)
		const dispatch = useDispatch();


		return(
			<Styles>
			<div className='topNavbar'>
				<button id='toggleNav' onClick={() => {
					dispatch(expandNav())
					console.log(isNavExpanded);
				}
				}>-</button>
				<ul className='tc'>
					<li><a href='#nomatch'>My Tabletop Hero</a></li>
				</ul>
			</div>

			<div className={isNavExpanded ? 'nav expand' : 'nav collapse'}>

				<ul>
					<li><a href='#notvalid'>My Account</a></li>
					<li><a href='#notvalid'>My Characters</a></li>
					<li><a href='#notvalid'>About MTTH</a></li>
					<li><a href='#notvalid'>Contact</a></li>
				</ul>
			</div>

			<div>
				<div className='contentBody'>
				</div>

			</div>
			</Styles>
		)
}

export default NavigationBar;
